INSERT INTO experiences (id, producer_id, title, description, duration, price, max_participants, image, location, includes, schedule_info, difficulty, min_age, created_at) VALUES
(1, 1, '제주 차밭 산책과 차 만들기 체험', '제주 유기농 차밭을 걸으며 차나무를 관찰하고, 직접 차를 따서 덖음차를 만들어보는 체험', 180, 45000, 15, '/images/experiences/jeju-tea-farm.jpg', '제주특별자치도 서귀포시 표선면', '차밭 가이드 투어, 차 제조 체험, 시음, 완성된 차 50g 포장', '매일 10:00, 14:00 (2회)', '초급', 8, '2024-01-10 00:00:00'),
(2, 4, '보성 녹차밭 명상 힐링', '드넓은 보성 녹차밭에서 명상과 요가를 하고 차 명상을 경험하는 힐링 프로그램', 120, 35000, 20, '/images/experiences/boseong-meditation.jpg', '전라남도 보성군 보성읍', '명상 가이드, 요가 매트, 차 명상 세션, 간식', '매주 토-일 09:00, 15:00', '초급', 12, '2024-01-10 00:00:00'),
(3, 5, '광주 전통 다기 만들기', '물레를 이용해 찻잔이나 주전자를 직접 만들고 초벌구이까지 체험', 150, 55000, 10, '/images/experiences/gwangju-pottery.jpg', '경기도 광주시 곤지암읍', '물레 체험, 성형, 초벌구이, 완성품 배송', '매주 화-일 10:00, 14:00', '초급', 10, '2024-01-10 00:00:00'),
(4, 7, '담양 대나무 다기 만들기', '대나무를 깎아 나만의 찻잔, 차통, 찻상을 만드는 수공예 체험', 120, 38000, 12, '/images/experiences/damyang-bamboo.jpg', '전라남도 담양군 담양읍', '대나무 선택, 깎기 도구 사용법, 제작 체험, 완성품 가져가기', '매주 토-일 10:00, 14:00', '중급', 12, '2024-01-10 00:00:00'),
(5, 3, '하동 야생차 채엽과 전통 제다', '지리산 자락의 야생차밭에서 손으로 차를 따고 전통 덖음 방식으로 차를 만드는 본격 체험', 240, 75000, 8, '/images/experiences/hadong-wild-tea.jpg', '경상남도 하동군 악양면', '야생차밭 트레킹, 채엽 체험, 전통 제다 실습, 점심 식사, 완성된 차 100g', '4-5월, 매주 토 09:00 (1회)', '중급', 14, '2024-01-10 00:00:00');
