(5, 2, '차의 이로운 점', '차의 건강 효능과 과학', '카테킨, 아미노산 등 성분 분석', '90분', 'beginner', 5, '/images/education/tea-benefits.jpg', '2024-01-01 00:00:00'),
(6, 2, '6대 차류 우리는 방법', '차 종류별 올바른 우리기', '온도, 시간, 양 조절법', '180분', 'intermediate', 6, '/images/education/brewing.jpg', '2024-01-01 00:00:00'),
(18, 2, '말차 제조와 활용', '말차의 제조 과정과 활용법', '말차 만들기와 요리 활용', '2시간', 'intermediate', 18, '/images/education/matcha.jpg', '2024-01-01 00:00:00'),
(22, 2, '녹차 블렌딩 마스터', '녹차 블렌딩 기술과 향미 조합', '다양한 녹차 블렌딩', '2시간 30분', 'advanced', 22, '/images/education/green-tea-blend.jpg', '2024-01-01 00:00:00'),
(26, 2, '발효차 제조 전문', '발효차의 제조 과정 마스터', '흑차, 보이차 제조', '3시간', 'advanced', 26, '/images/education/fermented-tea.jpg', '2024-01-01 00:00:00'),
(30, 2, '약용차 블렌딩', '건강을 위한 약용차 조합', '한약재와 차의 조화', '2시간', 'intermediate', 30, '/images/education/herbal-tea.jpg', '2024-01-01 00:00:00'),
(36, 2, '세계 차 문화의 전파', '차가 세계로 퍼진 역사와 각국의 차 문화', '중국에서 시작된 차가 실크로드를 통해 유럽으로, 바다를 건너 신대륙으로 전파되는 과정을 학습합니다.', '2시간', 'intermediate', 36, '/images/education/tea-world-spread.jpg', '2024-01-01 00:00:00'),
(37, 2, '현대 차 산업과 트렌드', '현대 차 산업의 동향과 미래', 'RTD 차 음료, 스페셜티 티, 친환경 차 등 현대 차 산업의 트렌드와 소비 패턴을 분석합니다.', '2시간', 'advanced', 37, '/images/education/tea-industry.jpg', '2024-01-01 00:00:00');

-- ====================
-- 3. 공예공부 (Category 3) - 8개
-- ====================

INSERT OR IGNORE INTO education_curriculum (id, category_id, title, description, content, duration, difficulty, display_order, thumbnail_image, created_at) VALUES
(7, 3, '공예의 역사', '한국 전통 공예의 역사', '삼국시대부터 현대까지', '90분', 'beginner', 1, '/images/education/craft-history.jpg', '2024-01-01 00:00:00'),
(8, 3, '한국공예의 시대별 변천사', '시대별 공예 양식의 변화', '고려청자부터 조선백자까지', '120분', 'intermediate', 2, '/images/education/craft-evolution.jpg', '2024-01-01 00:00:00'),
(9, 3, '도자기의 제작 기법', '전통 도자기 제작 과정', '물레 성형부터 가마 소성까지', '180분', 'advanced', 3, '/images/education/pottery-technique.jpg', '2024-01-01 00:00:00'),
