-- 4. 명상교육 (Category 4) - 12개
-- ====================

INSERT OR IGNORE INTO education_curriculum (id, category_id, title, description, content, duration, difficulty, display_order, thumbnail_image, created_at) VALUES
(14, 4, '명상의 역사', '고대부터 현대까지 명상의 발전', '동서양 명상 전통', '90분', 'beginner', 1, '/images/education/meditation-history.jpg', '2024-01-01 00:00:00'),
(15, 4, '명상의 정의와 원리', '명상의 개념과 작동 원리', '과학적 접근과 효과', '60분', 'beginner', 2, '/images/education/meditation-principle.jpg', '2024-01-01 00:00:00'),
(16, 4, '명상의 종류와 방법', '다양한 명상 기법 소개', '호흡, 마음챙김, 초월명상 등', '120분', 'intermediate', 3, '/images/education/meditation-types.jpg', '2024-01-01 00:00:00'),
(38, 4, '명상의 기원과 고대 역사', '명상의 시작과 고대 문명에서의 발전', '• 명상의 기원 (약 5,000년 전)\n- 인도 베다 시대의 명상 수행\n- 고대 이집트와 그리스의 명상 전통\n- 중국 도가의 내단 수련\n\n• 고대 명상 전통\n- 인도: 요가와 명상의 발전\n- 중국: 도교와 불교 명상\n- 티베트: 탄트라 명상', '2시간', 'beginner', 38, '/images/education/meditation-ancient.jpg', '2024-01-01 00:00:00'),
(39, 4, '불교 명상의 발전과 전파', '불교와 함께 발전한 명상 수행의 역사', '• 불교 명상의 발전\n- 붓다의 명상 수행과 깨달음\n- 위빠사나와 사마타 명상\n- 선불교의 탄생과 발전\n\n• 아시아로의 전파\n- 인도에서 동남아시아로\n- 중국 선불교의 발전\n- 한국과 일본으로의 전파', '2시간 30분', 'intermediate', 39, '/images/education/buddhist-meditation.jpg', '2024-01-01 00:00:00'),
(40, 4, '한국 전통 명상의 역사', '한국 고유의 명상 수행 방법과 발전', '• 삼국시대 명상 수행\n- 고구려: 도교와 불교의 융합\n- 백제: 왕실의 불교 지원\n- 신라: 원광법사의 세속오계와 명상\n\n• 화랑도의 수행\n- 풍류도: 유·불·선 융합\n- 심신 수련: 명상과 무술 결합\n\n• 고려와 조선의 명상 전통\n- 고려 시대: 선불교의 전성기\n- 조선 시대: 유교와의 조화\n\n• 현대 한국 명상\n- 전통 수행의 현대화\n- 명상 센터와 템플스테이', '2시간 30분', 'intermediate', 40, '/images/education/korean-meditation.jpg', '2024-01-01 00:00:00'),
(41, 4, '중국 도교 명상과 기공', '도교의 내단 수련과 기공 수행', '• 도교 명상의 특징\n- 내단(內丹): 내면의 연금술\n- 기(氣) 수련과 정기신(精氣神)\n- 자연과의 조화\n\n• 기공 수행\n- 정공(靜功): 정적 명상\n- 동공(動功): 동적 수련\n- 태극권과 명상\n\n• 도교 명상의 목표\n- 건강과 장수\n- 내면의 평화\n- 우주와의 합일', '2시간', 'intermediate', 41, '/images/education/taoist-meditation.jpg', '2024-01-01 00:00:00'),
(42, 4, '일본 선(禪)과 좌선 수행', '일본 선불교의 좌선 명상 전통', '• 일본 선의 발전\n- 에이사이와 린자이 선\n- 도겐과 소토 선\n- 무사도와 선의 결합\n\n• 좌선(坐禪) 수행\n- 자세와 호흡\n- 무념무상의 경지\n- 공안(公案) 수행\n\n• 일상 속 선\n- 선과 다도의 결합\n- 선 정원과 명상\n- 현대 생활 속 선', '2시간', 'intermediate', 42, '/images/education/zen-meditation.jpg', '2024-01-01 00:00:00'),
(43, 4, '명상의 서양 전파', '명상이 서양으로 전파된 역사와 현대화 과정', '• 서양으로의 전파\n- 19세기: 신지학 운동\n- 20세기 초: 요가와 명상의 소개\n- 1960년대: 명상 붐\n\n• 현대 명상의 발전\n- 초월명상(TM)\n- 마음챙김 명상(MBSR)\n- 뇌과학과 명상 연구\n\n• 서양식 명상의 특징\n- 종교성 탈피와 과학화\n- 스트레스 관리 도구\n- 기업과 학교에서의 활용', '2시간 30분', 'advanced', 43, '/images/education/western-meditation.jpg', '2024-01-01 00:00:00'),
(44, 4, '한중일 명상 문화 비교', '동아시아 3국의 명상 전통 비교', '• 명상의 뿌리\n- 인도에서 동아시아로\n- 각국의 토착 문화와 융합\n\n• 명상 철학 비교\n- 중국: 도교와 선불교\n- 한국: 유·불·도 융합\n- 일본: 선과 무사도\n\n• 수행 방법의 차이\n- 중국: 기공과 내단 수련\n- 한국: 참선과 염불 명상\n- 일본: 엄격한 좌선\n\n• 현대적 의미\n- 각국 명상의 세계화\n- 전통과 현대의 조화', '2시간 30분', 'advanced', 44, '/images/education/asian-meditation-comparison.jpg', '2024-01-01 00:00:00'),
(45, 4, '현대 명상과 마음챙김', '현대 사회에서의 명상 활용', '• 마음챙김 명상(MBSR)\n- 존 카밧진의 개발\n- 병원과 클리닉에서의 활용\n- 8주 프로그램 소개\n\n• 명상과 뇌과학\n- fMRI 연구 결과\n- 뇌의 변화와 신경가소성\n- 스트레스 감소 메커니즘\n\n• 일상 속 명상\n- 출퇴근 명상\n- 업무 중 짧은 명상\n- 디지털 디톡스와 명상\n\n• 명상 앱과 온라인 프로그램\n- Headspace, Calm 등\n- 가상현실 명상\n- AI 명상 가이드', '2시간', 'advanced', 45, '/images/education/modern-meditation.jpg', '2024-01-01 00:00:00'),
(46, 4, '차와 함께하는 명상', '차를 마시며 하는 명상 수행', '• 차 명상의 원리\n- 오감을 깨우는 차 마시기\n- 현재 순간에 집중\n- 차의 향과 맛으로 마음 챙김\n\n• 차 명상 실습\n- 차 준비 과정의 명상\n- 차를 마시는 명상\n- 차 후 여운 느끼기\n\n• 일상 속 차 명상\n- 아침 차 명상 루틴\n- 업무 중 차 휴식\n- 저녁 차 명상으로 하루 마무리', '90분', 'beginner', 46, '/images/education/tea-meditation.jpg', '2024-01-01 00:00:00');
